import React, { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

const API_URL = "http://localhost:4000/tasks";

export default function TodoApp() {
  const [tasks, setTasks] = useState([]);
  const [newTitle, setNewTitle] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(API_URL)
      .then(res => {
        if (!res.ok) throw new Error("Erro ao buscar tarefas");
        return res.json();
      })
      .then(data => {
        setTasks(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("ERRO FETCH:", err);
        setError("Erro ao buscar tarefas");
        setLoading(false);
      });
  }, []);

  function addTask() {
    if (!newTitle.trim()) return;
    const newTask = { title: newTitle.trim(), completed: false };
    fetch(API_URL, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newTask)
    })
      .then(res => res.json())
      .then(createdTask => {
        setTasks([...tasks, createdTask]);
        setNewTitle("");
      })
      .catch(() => setError("Erro ao criar tarefa"));
  }

  function toggleCompleted(task) {
    fetch(`${API_URL}/${task.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...task, completed: !task.completed })
    })
      .then(res => res.json())
      .then(updatedTask => {
        setTasks(tasks.map(t => (t.id === updatedTask.id ? updatedTask : t)));
      })
      .catch(() => setError("Erro ao atualizar tarefa"));
  }

  function deleteTask(id) {
    fetch(`${API_URL}/${id}`, {
      method: "DELETE"
    })
      .then(() => {
        setTasks(tasks.filter(t => t.id !== id));
      })
      .catch(() => setError("Erro ao deletar tarefa"));
  }

  if (loading) return <p className="m-3">Carregando tarefas...</p>;
  if (error) return <p className="m-3 text-danger">{error}</p>;

  return (
    <div className="container my-4" style={{ maxWidth: "600px" }}>
      <h2>Lista de Tarefas</h2>

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Nova tarefa"
          value={newTitle}
          onChange={e => setNewTitle(e.target.value)}
          onKeyDown={e => e.key === "Enter" && addTask()}
        />
        <button className="btn btn-primary" onClick={addTask}>Adicionar</button>
      </div>

      <ul className="list-group">
        {tasks.map(task => (
          <li
            key={task.id}
            className="list-group-item d-flex justify-content-between align-items-center"
          >
            <div>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleCompleted(task)}
                className="form-check-input me-2"
              />
              <span style={{ textDecoration: task.completed ? "line-through" : "none" }}>
                {task.title}
              </span>
            </div>
            <button
              className="btn btn-danger btn-sm"
              onClick={() => deleteTask(task.id)}
            >
              Apagar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
